/**
 * Type definitions for CSS modules and assets
 */

declare module "*.css" {
  const content: any;
  export default content;
}

declare module "*.scss" {
  const content: any;
  export default content;
}

declare module "*.sass" {
  const content: any;
  export default content;
}

declare module "*.less" {
  const content: any;
  export default content;
}
